import api from "@/api";
const state = {
  user: {},
  role: [],
};

const mutations = {
  UPDATE_USER: (state, data) => {
    state.user = data;
  },
  UPDATE_ROLE: (state, data) => {
    state.role = data;
  },
};

const actions = {
  updateUser(store) {
    return api.getUser({}).then((res) => {
      let { role } = res;
      store.commit("UPDATE_ROLE", role);
      store.commit("UPDATE_USER", res);
    });
  },
};

export default {
  state,
  mutations,
  actions,
};
